<?phpclass ListItem{    private $m_sList;    public function __set($p_sProperty, $p_vValue)    {        switch ($p_sProperty) {            case "AddList":                if ($p_vValue == "") {                    throw new Exception("De lijst moet een naam hebben");                }                $this->m_sList = $p_vValue;                break;            case "DelList":                $this->m_sList = $p_vValue;                break;        }    }    public function __get($p_sProperty)    {        switch ($p_sProperty) {            case "AddList":                return $this->m_sList;                break;            case "DelList":                return $this->m_sList;                break;        }    }    public function SaveList(){            try{                $conn = new PDO("mysql:host=localhost; dbname=period", "root", "");            $statement = $conn->prepare("INSERT INTO lists (listitem) VALUES (:listitem)");            $statement->bindValue(":listitem", $this->m_sList);            $res = $statement->execute();            return ( $res );            }            catch( Exception $e ){                $error = $e->getMessage();            }    }    public function DeleteList(){        try{            $conn = new PDO("mysql:host=localhost; dbname=period", "root", "");            $statement = $conn->prepare("DELETE FROM lists WHERE listitem = :listitem");            $statement->bindValue(":listitem", $this->m_sList);            $statement->execute();        }        catch( Exception $e ){            echo $e->getMessage();        }    }    public function GetLists(){        try {            $conn = new PDO("mysql:host=localhost;dbname=period", "root", "");            $statement = $conn->prepare("select * from lists");            $statement->execute();            $result = $statement->fetchAll();            return $result;        }        catch( Exception $e ){            echo $e->getMessage();        }    }}